import { useState, useEffect } from 'react';
import axios from 'axios';

function Productdetails() {
    const [data, setData] = useState([]);
    const [productData, setproductData] = useState({
        productName: '',
        price: '',
        description: ''
    });

    console.log(productData, "productData");


    const getProduct = async () => {

        try {
            // const productResponse = await axios.get('http://localhost:4000/product');
            const response = await fetch('http://localhost:4000/product');
            const pdata = await response.json()
            setData(pdata?.data?.findProduct);
        }
        catch (error) {
            console.log(error.message);
            console.error(error, "product datails are not fetched");

        }
    };


    const postProduct = async (e) => {
        try {
            // const response = await fetch('http://localhost:4000/product',
            //     {
            //         method: 'post',
            //         body: JSON.stringify({ productName: '', price: '', description: '' })
            //     });
            // console.log(response, 'res');
            e.preventDefault()
            console.log('postProduct');


            const res = await fetch('http://localhost:4000/product', {
                method: 'POST',
                headers: {
                    'Accept': 'application/json',
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    productName: '',
                    price: '',
                    description: ''
                })

            })
            console.log(res, 'res');
        }
        catch (error) {
            console.log("product is not posted");
        }
    };
    const handleChange = (e) => {
        const { name, value } = e.target;

        setproductData({...productData, [name]:value})

    }

    useEffect(() => {
        getProduct();
    }, []);

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th> ProductName</th>
                        <th> Price</th>
                        <th> desciption</th>
                    </tr>
                </thead>
                <tbody>
                    {data && data.map((ele, index) => {
                        return (

                            <tr key={ele._id}>
                                <td>{ele.productName}</td>
                                <td>{ele.price}</td>
                                <td>{ele.description}</td>
                            </tr>)

                    })}
                </tbody>

            </table>
            <br></br>
            <br></br>


            {/* to post data  */}
            <form onSubmit={postProduct}>
                <div className='form-group'>
                    <label>productName::</label>
                    <input
                        type='text'
                        value={productData.productName}
                        name="productName"
                        placeholder='enter the product name'
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label> price::</label>
                    <input
                        type='number'
                        value={productData.price}
                        name='price'
                        placeholder='price of the product'
                        onChange={handleChange}
                    />
                </div>
                <div>
                    <label> description::</label>
                    <textarea
                        type="text"
                        value={productData.description}
                        name='description'
                        placeholder='description of the product'
                        onChange={handleChange}
                    />
                </div>
                <button type='submit'>Submit</button>

            </form>



        </div>
    );
}
export default Productdetails;
