import { useState, useEffect } from 'react';
import axios from 'axios';

function Productdetails() {
    const [data, setData] = useState([]);
    const [postData, setpostdata] = useState();
    const [updatedata, setUpdatedata] = useState({
        productName: '',
        price: '',
        description: ''
    });

    const getProduct = async () => {

        try {
            // const productResponse = await axios.get('http://localhost:4000/product');
            const response = await fetch('http://localhost:4000/product');
            const pdata = await response.json()
            setData(pdata?.data?.findProduct);
        }
        catch (error) {
            console.log(error.message);
            console.error(error, "product datails are not fetched");

        }
    };


    const postProduct = async () => {
        try {
            const response = await fetch('http://localhost:4000/product',
                {
                    method: 'post',
                    body: json.stringify({ productName: '', price: '', description: '' })
                });
                console.log(response, '');

            // const pdata = await response.json()
            // setData(pdata?.data?.findProduct);
            // setData(response.pdata, ...data)
        }
        catch (error) {
            console.log("product is not posted");
        }
    };


    useEffect(() => {
        getProduct();
    }, []);

    return (
        <div>
            <table>
                <thead>
                    <tr>
                        <th> ProductName</th>
                        <th> Price</th>
                        <th> desciption</th>
                    </tr>
                </thead>
                <tbody>
                    {data && data.map((ele, index) => {
                        return (

                            <tr key={ele._id}>
                                <td>{ele.productName}</td>
                                <td>{ele.price}</td>
                                <td>{ele.description}</td>
                            </tr>)

                    })}
                </tbody>

            </table>
            <br></br>
            <br></br>
            {/* to post data  */}
            <form>
                <div className='form-group'>
                    <label>productName::</label>
                    <input
                        type='text'
                        value={updatedata.productName}
                        name="productName"
                        placeholder='enter the product name'
                        onChange={(e) => setUpdatedata(e.target.value)} />
                </div>
                <div>
                    <label> price::</label>
                    <input
                        type='number'
                        value={updatedata.price}
                        name='price'
                        placeholder='price of the product'
                        onChange={(e) => setUpdatedata(e.target.value)} />
                </div>
                <div>
                    <label> description::</label>
                    <textarea
                        type="text"
                        value={updatedata.description}
                        name='description'
                        placeholder='description of the product'
                        onChange={(e) => setUpdatedata(e.target.value)} />
                </div>
                <button onClick={postProduct}>POST</button>

            </form>



        </div>
    );
}
export default Productdetails;
